const pool = require("../models/db.js"); // Importar a conexão com o banco de dados

const getDisciplinas = async (req, res) => {
  try {
    const result = await pool.query(
      "SELECT * FROM disciplinas WHERE ativo = TRUE"
    );
    res.json(result.rows);
  } catch (error) {
    res.status(500).json({ error: "Erro ao buscar disciplinas" });
  }
};

const createDisciplina = async (req, res) => {
  const { nome } = req.body;
  try {
    await pool.query("INSERT INTO disciplinas (nome) VALUES ($1)", [nome]);
    res.status(201).send("Disciplina criada com sucesso");
  } catch (error) {
    res.status(500).json({ error: "Erro ao criar disciplina" });
  }
};

const updateDisciplina = async (req, res) => {
  const { id } = req.params;
  const { nome } = req.body;
  try {
    await pool.query("UPDATE disciplinas SET nome = $1 WHERE id = $2", [
      nome,
      id,
    ]);
    res.send("Disciplina atualizada com sucesso");
  } catch (error) {
    res.status(500).json({ error: "Erro ao atualizar disciplina" });
  }
};

const deleteDisciplina = async (req, res) => {
  const { id } = req.params;
  try {
    await pool.query("UPDATE disciplinas SET ativo = FALSE WHERE id = $1", [
      id,
    ]);
    res.send("Disciplina excluída com sucesso");
  } catch (error) {
    res.status(500).json({ error: "Erro ao excluir disciplina" });
  }
};

module.exports = {
  getDisciplinas,
  createDisciplina,
  updateDisciplina,
  deleteDisciplina,
};
