const pool = require("../models/db");

const getSalas = async (req, res) => {
  try {
    const result = await pool.query("SELECT * FROM salas WHERE ativo = TRUE");
    res.json(result.rows);
  } catch (error) {
    res.status(500).json({ error: "Erro ao buscar salas" });
  }
};

const createSala = async (req, res) => {
  const { nome } = req.body;
  try {
    await pool.query("INSERT INTO salas (nome) VALUES ($1)", [nome]);
    res.status(201).send("Sala criada com sucesso");
  } catch (error) {
    res.status(500).json({ error: "Erro ao criar sala" });
  }
};

const updateSala = async (req, res) => {
  const { id } = req.params;
  const { nome } = req.body;
  try {
    await pool.query("UPDATE salas SET nome = $1 WHERE id = $2", [nome, id]);
    res.send("Sala atualizada com sucesso");
  } catch (error) {
    res.status(500).json({ error: "Erro ao atualizar sala" });
  }
};

const deleteSala = async (req, res) => {
  const { id } = req.params;
  try {
    await pool.query("UPDATE salas SET ativo = FALSE WHERE id = $1", [id]);
    res.send("Sala exclu√≠da com sucesso");
  } catch (error) {
    res.status(500).json({ error: "Erro ao excluir sala" });
  }
};

module.exports = {
  getSalas,
  createSala,
  updateSala,
  deleteSala,
};
