const pool = require("../models/db");

const getTurmas = async (req, res) => {
  try {
    const result = await pool.query("SELECT * FROM turmas WHERE ativo = TRUE");
    res.json(result.rows);
  } catch (error) {
    res.status(500).json({ error: "Erro ao buscar turmas" });
  }
};

const createTurma = async (req, res) => {
  const { nome } = req.body;
  try {
    await pool.query("INSERT INTO turmas (nome) VALUES ($1)", [nome]);
    res.status(201).send("Turma criada com sucesso");
  } catch (error) {
    res.status(500).json({ error: "Erro ao criar turma" });
  }
};

const updateTurma = async (req, res) => {
  const { id } = req.params;
  const { nome } = req.body;
  try {
    await pool.query("UPDATE turmas SET nome = $1 WHERE id = $2", [nome, id]);
    res.send("Turma atualizada com sucesso");
  } catch (error) {
    res.status(500).json({ error: "Erro ao atualizar turma" });
  }
};

const deleteTurma = async (req, res) => {
  const { id } = req.params;
  try {
    await pool.query("UPDATE turmas SET ativo = FALSE WHERE id = $1", [id]);
    res.send("Turma exclu√≠da com sucesso");
  } catch (error) {
    res.status(500).json({ error: "Erro ao excluir turma" });
  }
};

module.exports = {
  getTurmas,
  createTurma,
  updateTurma,
  deleteTurma,
};
